const formData = context.component?.variables?.['formData'] || {};
const nickname = formData.nickname?.trim();
const email = formData.email?.trim();

const isNicknameValid = nickname && nickname.length > 0;
const isEmailValid = email && /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

const isValid = isNicknameValid && isEmailValid;
context.component.variables['isFormValid'] = isValid;

return isValid;
